# Script for populating the database. You can run it as:
#
#     mix run priv/repo/seeds/bncc.exs
#
# Inside the script, you can read and write to any of your
# repositories directly:
#
#     Lanttern.Repo.insert!(%Lanttern.SomeSchema{})
#
# We recommend using the bang functions (`insert!`, `update!`
# and so on) as they will fail if something goes wrong.

alias Lanttern.Repo

alias Lanttern.Curricula.Curriculum
alias Lanttern.Curricula.CurriculumComponent
alias Lanttern.Curricula.CurriculumItem
alias Lanttern.Curricula.CurriculumRelationship
alias Lanttern.Taxonomy

# ------------------------------
# helper functions
# ------------------------------

# use to insert components
insert_comp = fn params ->
  %CurriculumComponent{}
  |> CurriculumComponent.changeset(params)
  |> Repo.insert!()
end

# use to insert items when there's no risk of duplication
insert_item = fn params ->
  %CurriculumItem{}
  |> CurriculumItem.changeset(params)
  |> Repo.insert!()
end

# use to insert potentially duplicated items (e.g. Campos de Atuação, Unidade Temática)
get_or_insert_item = fn %{name: name, curriculum_component_id: cc_id} = params ->
  case Repo.get_by(CurriculumItem, [name: name, curriculum_component_id: cc_id]) do
    nil -> insert_item.(params)
    curriculum_item -> curriculum_item
  end
end

insert_relationship = fn item_a_id, item_b_id ->
  Repo.insert!(%CurriculumRelationship{curriculum_item_a_id: item_a_id, curriculum_item_b_id: item_b_id, type: "hierarchical"})
end

