insert_item.(%{curriculum_component_id: comp_lp.id, name: "Analisar informações, argumentos e opiniões manifestados em interações sociais e nos meios de comunicação, posicionando-se ética e criticamente em relação a conteúdos discriminatórios que ferem direitos humanos e ambientais.", subjects_ids: lp})
insert_item.(%{curriculum_component_id: comp_lp.id, name: "Selecionar textos e livros para leitura integral, de acordo com objetivos, interesses e projetos pessoais (estudo, formação pessoal, entretenimento, pesquisa, trabalho etc.).", subjects_ids: lp})
insert_item.(%{curriculum_component_id: comp_lp.id, name: "Envolver-se em práticas de leitura literária que possibilitem o desenvolvimento do senso estético para fruição, valorizando a literatura e outras manifestações artístico-culturais como formas de acesso às dimensões lúdicas, de imaginário e encantamento, reconhecendo o potencial transformador e humanizador da experiência com a literatura.", subjects_ids: lp})
insert_item.(%{curriculum_component_id: comp_lp.id, name: "Mobilizar práticas da cultura digital, diferentes linguagens, mídias e ferramentas digitais para expandir as formas de produzir sentidos (nos processos de compreensão e produção), aprender e refletir sobre o mundo e realizar diferentes projetos autorais.", subjects_ids: lp})

# Competências específicas de Arte
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Explorar, conhecer, fruir e analisar criticamente práticas e produções artísticas e culturais do seu entorno social, dos povos indígenas, das comunidades tradicionais brasileiras e de diversas sociedades, em distintos tempos e espaços, para reconhecer a arte como um fenômeno cultural, histórico, social e sensível a diferentes contextos e dialogar com as diversidades.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Compreender as relações entre as linguagens da Arte e suas práticas integradas, inclusive aquelas possibilitadas pelo uso das novas tecnologias de informação e comunicação, pelo cinema e pelo audiovisual, nas condições particulares de produção, na prática de cada linguagem e nas suas articulações.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Pesquisar e conhecer distintas matrizes estéticas e culturais – especialmente aquelas manifestas na arte e nas culturas que constituem a identidade brasileira –, sua tradição e manifestações contemporâneas, reelaborando-as nas criações em Arte.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Experienciar a ludicidade, a percepção, a expressividade e a imaginação, ressignificando espaços da escola e de fora dela no âmbito da Arte.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Mobilizar recursos tecnológicos como formas de registro, pesquisa e criação artística.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Estabelecer relações entre arte, mídia, mercado e consumo, compreendendo, de forma crítica e problematizadora, modos de produção e de circulação da arte na sociedade.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Problematizar questões políticas, sociais, econômicas, científicas, tecnológicas e culturais, por meio de exercícios, produções, intervenções e apresentações artísticas.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Desenvolver a autonomia, a crítica, a autoria e o trabalho coletivo e colaborativo nas artes.", subjects_ids: ar})
insert_item.(%{curriculum_component_id: comp_ar.id, name: "Analisar e valorizar o patrimônio artístico nacional e internacional, material e imaterial, com suas histórias e diferentes visões de mundo.", subjects_ids: ar})

# Competências específicas de Educação Física
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Compreender a origem da cultura corporal de movimento e seus vínculos com a organização da vida coletiva e individual.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Planejar e empregar estratégias para resolver desafios e aumentar as possibilidades de aprendizagem das práticas corporais, além de se envolver no processo de ampliação do acervo cultural nesse campo.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Refletir, criticamente, sobre as relações entre a realização das práticas corporais e os processos de saúde/doença, inclusive no contexto das atividades laborais.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Identificar a multiplicidade de padrões de desempenho, saúde, beleza e estética corporal, analisando, criticamente, os modelos disseminados na mídia e discutir posturas consumistas e preconceituosas.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Identificar as formas de produção dos preconceitos, compreender seus efeitos e combater posicionamentos discriminatórios em relação às práticas corporais e aos seus participantes.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Interpretar e recriar os valores, os sentidos e os significados atribuídos às diferentes práticas corporais, bem como aos sujeitos que delas participam.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Reconhecer as práticas corporais como elementos constitutivos da identidade cultural dos povos e grupos.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Usufruir das práticas corporais de forma autônoma para potencializar o envolvimento em contextos de lazer, ampliar as redes de sociabilidade e a promoção da saúde.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Reconhecer o acesso às práticas corporais como direito do cidadão, propondo e produzindo alternativas para sua realização no contexto comunitário.", subjects_ids: ef})
insert_item.(%{curriculum_component_id: comp_ef.id, name: "Experimentar, desfrutar, apreciar e criar diferentes brincadeiras, jogos, danças, ginásticas, esportes, lutas e práticas corporais de aventura, valorizando o trabalho coletivo e o protagonismo.", subjects_ids: ef})

# Competências específicas de Língua Inglesa
insert_item.(%{curriculum_component_id: comp_li.id, name: "Identificar o lugar de si e o do outro em um mundo plurilíngue e multicultural, refletindo, criticamente, sobre como a aprendizagem da língua inglesa contribui para a inserção dos sujeitos no mundo globalizado, inclusive no que concerne ao mundo do trabalho.", subjects_ids: li})
insert_item.(%{curriculum_component_id: comp_li.id, name: "Comunicar-se na língua inglesa, por meio do uso variado de linguagens em mídias impressas ou digitais, reconhecendo-a como ferramenta de acesso ao conhecimento, de ampliação das perspectivas e de possibilidades para a compreensão dos valores e interesses de outras culturas e para o exercício do protagonismo social.", subjects_ids: li})
insert_item.(%{curriculum_component_id: comp_li.id, name: "Identificar similaridades e diferenças entre a língua inglesa e a língua materna/outras línguas, articulando-as a aspectos sociais, culturais e identitários, em uma relação intrínseca entre língua, cultura e identidade.", subjects_ids: li})
insert_item.(%{curriculum_component_id: comp_li.id, name: "Elaborar repertórios linguístico-discursivos da língua inglesa, usados em diferentes países e por grupos sociais distintos dentro de um mesmo país, de modo a reconhecer a diversidade linguística como direito e valorizar os usos heterogêneos, híbridos e multimodais emergentes nas sociedades contemporâneas.", subjects_ids: li})
insert_item.(%{curriculum_component_id: comp_li.id, name: "Utilizar novas tecnologias, com novas linguagens e modos de interação, para pesquisar, selecionar, compartilhar, posicionar-se e produzir sentidos em práticas de letramento na língua inglesa, de forma ética, crítica e responsável.", subjects_ids: li})
insert_item.(%{curriculum_component_id: comp_li.id, name: "Conhecer diferentes patrimônios culturais, materiais e imateriais, difundidos na língua inglesa, com vistas ao exercício da fruição e da ampliação de perspectivas no contato com diferentes manifestações artístico-culturais.", subjects_ids: li})

#Competências específicas de Geografia
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Utilizar os conhecimentos geográficos para entender a interação sociedade/natureza e exercitar o interesse e o espírito de investigação e de resolução de problemas.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Estabelecer conexões entre diferentes temas do conhecimento geográfico, reconhecendo a importância dos objetos técnicos para a compreensão das formas como os seres humanos fazem uso dos recursos da natureza ao longo da história.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Desenvolver autonomia e senso crítico para compreensão e aplicação do raciocínio geográfico na análise da ocupação humana e produção do espaço, envolvendo os princípios de analogia, conexão, diferenciação, distribuição, extensão, localização e ordem.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Desenvolver o pensamento espacial, fazendo uso das linguagens cartográficas e iconográficas, de diferentes gêneros textuais e das geotecnologias para a resolução de problemas que envolvam informações geográficas.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Desenvolver e utilizar processos, práticas e procedimentos de investigação para compreender o mundo natural, social, econômico, político e o meio técnico-científico e informacional, avaliar ações e propor perguntas e soluções (inclusive tecnológicas) para questões que requerem conhecimentos científicos da Geografia.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Construir argumentos com base em informações geográficas, debater e defender ideias e pontos de vista que respeitem e promovam a consciência socioambiental e o respeito à biodiversidade e ao outro, sem preconceitos de qualquer natureza.", subjects_ids: ge})
insert_item.(%{curriculum_component_id: comp_ge.id, name: "Agir pessoal e coletivamente com respeito, autonomia, responsabilidade, flexibilidade, resiliência e determinação, propondo ações sobre as questões socioambientais, com base em princípios éticos, democráticos, sustentáveis e solidários.", subjects_ids: ge})

# Competências específicas de História
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Compreender e problematizar os conceitos e procedimentos norteadores da produção historiográfica.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Compreender acontecimentos históricos, relações de poder e processos e mecanismos de transformação e manutenção das estruturas sociais, políticas, econômicas e culturais ao longo do tempo e em diferentes espaços para analisar, posicionar-se e intervir no mundo contemporâneo.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Compreender a historicidade no tempo e no espaço, relacionando acontecimentos e processos de transformação e manutenção das estruturas sociais, políticas, econômicas e culturais, bem como problematizar os significados das lógicas de organização cronológica.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Elaborar questionamentos, hipóteses, argumentos e proposições em relação a documentos, interpretações e contextos históricos específicos, recorrendo a diferentes linguagens e mídias, exercitando a empatia, o diálogo, a resolução de conflitos, a cooperação e o respeito.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Identificar interpretações que expressem visões de diferentes sujeitos, culturas e povos com relação a um mesmo contexto histórico, e posicionar-se criticamente com base em princípios éticos, democráticos, inclusivos, sustentáveis e solidários.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Analisar e compreender o movimento de populações e mercadorias no tempo e no espaço e seus significados históricos, levando em conta o respeito e a solidariedade com as diferentes populações.", subjects_ids: hi})
insert_item.(%{curriculum_component_id: comp_hi.id, name: "Produzir, avaliar e utilizar tecnologias digitais de informação e comunicação de modo crítico, ético e responsável, compreendendo seus significados para os diferentes grupos ou estratos sociais.", subjects_ids: hi})

